function run(statusTake) {
  var resposta;

  // Verifica o valor da variável `statusTake` para definir a mensagem de resposta
  if (statusTake === 401) {
    resposta = "Erro: Não autorizado. Verifique o token de acesso.";
  } else if (statusTake === 403) {
    resposta = "Erro: Limite de requisições atingido. Tente novamente mais tarde.";
  } else if (statusTake === 404) {
    resposta = "Erro: O recurso solicitado não foi encontrado.";
  } else if (statusTake >= 500) {
    resposta = "Erro: Problema no servidor. Por favor, tente novamente mais tarde.";
  } else {
    resposta = "Erro inesperado: Não foi possível processar sua solicitação. Tente novamente mais tarde.";
  }

  // Retorna a resposta para exibição no fluxo do Blip
  return resposta;
}

function run(repositories) {
  // Converte a string JSON para um array de objetos JavaScript
  var reposArray = JSON.parse(repositories);

  // Pega o primeiro item do array
  var firstRepo = reposArray[0];

  // Armazena as informações do primeiro repositório em uma variável
  var firstRepoData = {
    full_name: firstRepo.full_name,
    description: firstRepo.description,
    avatar_url: firstRepo.avatar_url,
    html_url: firstRepo.html_url
  };

  // Retorna o objeto com os dados do primeiro repositório como uma string JSON
  return JSON.stringify(firstRepoData);
}

